parameters.query:
-- Clean up metrics older than 30 days
DELETE FROM metrics_cpu WHERE timestamp < NOW() - INTERVAL '30 days';
DELETE FROM metrics_ram WHERE timestamp < NOW() - INTERVAL '30 days';
DELETE FROM metrics_gpu WHERE timestamp < NOW() - INTERVAL '30 days';
DELETE FROM metrics_temperature WHERE timestamp < NOW() - INTERVAL '30 days';
DELETE FROM metrics_disk WHERE timestamp < NOW() - INTERVAL '30 days';

-- Return count of remaining rows
SELECT 
  (SELECT COUNT(*) FROM metrics_cpu) as cpu_count,
  (SELECT COUNT(*) FROM metrics_ram) as ram_count,
  (SELECT COUNT(*) FROM metrics_gpu) as gpu_count,
  (SELECT COUNT(*) FROM metrics_temperature) as temp_count,
  (SELECT COUNT(*) FROM metrics_disk) as disk_count;

-- Clean up old sessions (expired more than 7 days ago)
DELETE FROM active_sessions WHERE expires_at < NOW() - INTERVAL '7 days';

-- Clean up old blacklisted tokens (expired more than 24 hours ago)
DELETE FROM token_blacklist WHERE expires_at < NOW() - INTERVAL '24 hours';

-- Return cleanup stats
SELECT 
  (SELECT COUNT(*) FROM active_sessions) as active_sessions,
  (SELECT COUNT(*) FROM token_blacklist) as blacklisted_tokens;

-- Clean up audit logs older than 90 days
DELETE FROM api_audit_logs WHERE timestamp < NOW() - INTERVAL '90 days';
DELETE FROM telegram_audit_logs WHERE timestamp < NOW() - INTERVAL '90 days';

-- Clean up old self-healing events (older than 30 days)
DELETE FROM self_healing_events WHERE timestamp < NOW() - INTERVAL '30 days';

-- Return counts
SELECT 
  (SELECT COUNT(*) FROM api_audit_logs) as api_audit_count,
  (SELECT COUNT(*) FROM telegram_audit_logs) as telegram_audit_count,
  (SELECT COUNT(*) FROM self_healing_events) as healing_events_count;

-- Vacuum and analyze tables for performance
VACUUM ANALYZE metrics_cpu;
VACUUM ANALYZE metrics_ram;
VACUUM ANALYZE metrics_gpu;
VACUUM ANALYZE metrics_temperature;
VACUUM ANALYZE metrics_disk;
VACUUM ANALYZE conversations;
VACUUM ANALYZE messages;
VACUUM ANALYZE documents;
VACUUM ANALYZE active_sessions;
VACUUM ANALYZE api_audit_logs;

-- Return database size stats
SELECT 
  pg_database_size('arasul_db') as db_size_bytes,
  pg_size_pretty(pg_database_size('arasul_db')) as db_size_human;

parameters.functionCode:
const metricsResult = items[0].json;
const sessionsResult = items[1].json;
const auditResult = items[2].json;
const vacuumResult = items[3].json;

const timestamp = new Date().toISOString();

const report = {
  timestamp,
  status: 'completed',
  metrics: {
    cpu_rows: metricsResult[0]?.cpu_count || 0,
    ram_rows: metricsResult[0]?.ram_count || 0,
    gpu_rows: metricsResult[0]?.gpu_count || 0,
    temp_rows: metricsResult[0]?.temp_count || 0,
    disk_rows: metricsResult[0]?.disk_count || 0
  },
  sessions: {
    active_sessions: sessionsResult[0]?.active_sessions || 0,
    blacklisted_tokens: sessionsResult[0]?.blacklisted_tokens || 0
  },
  audit: {
    api_audit_count: auditResult[0]?.api_audit_count || 0,
    telegram_audit_count: auditResult[0]?.telegram_audit_count || 0,
    healing_events_count: auditResult[0]?.healing_events_count || 0
  },
  database: {
    size_bytes: vacuumResult[0]?.db_size_bytes || 0,
    size_human: vacuumResult[0]?.db_size_human || 'unknown'
  }
};

return [{ json: report }];

