const metrics = items[0]?.json;
const services = items[1]?.json;
const alerts = [];

// Thresholds (can be moved to env vars)
const thresholds = {
  cpu: { warning: 80, critical: 95 },
  ram: { warning: 85, critical: 95 },
  gpu: { warning: 85, critical: 95 },
  temperature: { warning: 75, critical: 85 },
  disk: { warning: 80, critical: 90 }
};

// Check metrics
if (metrics && !metrics.error) {
  // CPU check
  if (metrics.cpu >= thresholds.cpu.critical) {
    alerts.push({
      type: 'cpu_critical',
      severity: 'critical',
      source: 'health-check',
      message: 'CPU usage critically high',
      value: metrics.cpu,
      threshold: thresholds.cpu.critical
    });
  } else if (metrics.cpu >= thresholds.cpu.warning) {
    alerts.push({
      type: 'cpu_warning',
      severity: 'warning',
      source: 'health-check',
      message: 'CPU usage elevated',
      value: metrics.cpu,
      threshold: thresholds.cpu.warning
    });
  }

  // RAM check
  if (metrics.ram >= thresholds.ram.critical) {
    alerts.push({
      type: 'ram_critical',
      severity: 'critical',
      source: 'health-check',
      message: 'RAM usage critically high',
      value: metrics.ram,
      threshold: thresholds.ram.critical
    });
  } else if (metrics.ram >= thresholds.ram.warning) {
    alerts.push({
      type: 'ram_warning',
      severity: 'warning',
      source: 'health-check',
      message: 'RAM usage elevated',
      value: metrics.ram,
      threshold: thresholds.ram.warning
    });
  }

  // GPU check
  if (metrics.gpu >= thresholds.gpu.critical) {
    alerts.push({
      type: 'gpu_critical',
      severity: 'critical',
      source: 'health-check',
      message: 'GPU usage critically high',
      value: metrics.gpu,
      threshold: thresholds.gpu.critical
    });
  } else if (metrics.gpu >= thresholds.gpu.warning) {
    alerts.push({
      type: 'gpu_warning',
      severity: 'warning',
      source: 'health-check',
      message: 'GPU usage elevated',
      value: metrics.gpu,
      threshold: thresholds.gpu.warning
    });
  }

  // Temperature check
  if (metrics.temperature >= thresholds.temperature.critical) {
    alerts.push({
      type: 'temperature_critical',
      severity: 'critical',
      source: 'health-check',
      message: 'System temperature critically high',
      value: metrics.temperature,
      threshold: thresholds.temperature.critical
    });
  } else if (metrics.temperature >= thresholds.temperature.warning) {
    alerts.push({
      type: 'temperature_warning',
      severity: 'warning',
      source: 'health-check',
      message: 'System temperature elevated',
      value: metrics.temperature,
      threshold: thresholds.temperature.warning
    });
  }

  // Disk check
  const diskPercent = metrics.disk?.percent || 0;
  if (diskPercent >= thresholds.disk.critical) {
    alerts.push({
      type: 'disk_critical',
      severity: 'critical',
      source: 'health-check',
      message: 'Disk usage critically high',
      value: diskPercent,
      threshold: thresholds.disk.critical
    });
  } else if (diskPercent >= thresholds.disk.warning) {
    alerts.push({
      type: 'disk_warning',
      severity: 'warning',
      source: 'health-check',
      message: 'Disk usage elevated',
      value: diskPercent,
      threshold: thresholds.disk.warning
    });
  }
}

// Check services
if (services && services.services) {
  for (const [name, status] of Object.entries(services.services)) {
    if (status.status === 'unhealthy' || status.status === 'down') {
      alerts.push({
        type: 'service_down',
        severity: 'critical',
        source: 'health-check',
        message: `Service ${name} is ${status.status}`,
        metadata: { service: name, status: status.status }
      });
    }
  }
}

return alerts.length > 0 ? alerts.map(a => ({ json: a })) : [{ json: { noAlerts: true } }];
